import React from 'react';
export const TodoItem = ({ todo, onDelete }) => {
    return (
        <>
            <div className="p-4 mb-4 bg-white rounded shadow-md">
                <h4 className="text-lg font-semibold text-gray-800">{todo.title}</h4>
                <p className="text-gray-600">{todo.desc}</p>
                <button
                    className="mt-3 px-4 py-1 bg-red-700 text-white text-sm rounded hover:bg-red-600"
                    onClick={() => { onDelete(todo); }}
                >
                    Delete
                </button>
            </div>
            <hr className="my-4 border-gray-300" />
        </>
    );
};
